import React, { Component } from 'react'

class EventBind extends Component {
    constructor(props) {
        super(props)

        this.state = {
             message: 'Hello'
        }

        // For approach 2
        this.approach2Handlar = this.approach2Handlar.bind(this)
    }
    approach1Handlar(){
        this.setState({
            message: 'GoodBye!'
        })
    }

    approach2Handlar(){
        this.setState({
            message: 'GoodBye!'
        })
    }

    render() {
        return (
            <div>
                <h1>{this.state.message}</h1>
                <button>Click</button>
                <button onClick={this.approach1Handlar.bind(this)}>Approach 1</button>
                <button onClick={this.approach1Handlar}>Approach 2</button>
                <button>Approach 3</button>
                <button>Approach 4</button>
            </div>
        )
    }
}

export default EventBind
